import TasksList from "./components/TasksList";
import "./App.css";
import { useState } from "react";

function App() {
  [todoList, setTodoList] = useState([]);

  return (
    <>
      <div className="header">
        <h1>Task Manager</h1>
      </div>
      <div className="flex-container">
        <TasksList list={todoList} />
      </div>
    </>
  );
}

export default App;
